mapper.delete:
	delete from finance_manage_receipt 


            order_source_code = #{code,jdbcType=VARCHAR}


            and waybill_id = #{waybillId,jdbcType=BIGINT}


            and pay_way != #{payway,jdbcType=INTEGER}

delete from finance_manage_receipt


        order_source_code = #{code,jdbcType=VARCHAR}


        and waybill_id = #{waybillId,jdbcType=BIGINT}


        and pay_way = #{payway,jdbcType=INTEGER}

mapper.update:
UPDATE finance_manage_receipt




                when id = #{i.id, jdbcType = BIGINT} then #{i.deliveryStatus, jdbcType = INTEGER}






                when id = #{i.id, jdbcType = BIGINT} then #{i.receiptedAmount, jdbcType = DECIMAL}






                when id = #{i.id, jdbcType = BIGINT} then #{i.unreceiptAmount, jdbcType = DECIMAL}




WHERE

    id = #{i.id, jdbcType = BIGINT}

UPDATE finance_manage_receipt
	set delivery_status = #{deliveryStatus},
	receipted_amount = #{receiptedAmount},
	unreceipt_amount = #{unreceiptAmount}
	where id = #{id}
